mapper.update:
update user
set password         = #{password}
  , email            = #{email}
  , random_name      = #{randomName}
  , aware_role       = #{awareRole}
  , role             = #{role}
  , prev_contributor = #{prevContributor}
  , prev_receiver    = #{prevReceiver}
where id = #{id}

mapper.select:
select aware_role
from user
where id = #{id}
